fields.extra_css_code:
.text {
	line-height: 1.1em;
	white-space: nowrap;
}
.title {
	font-size: 128px;
}
.subtitle {
	font-size: 92px;
}
.body {
	font-size: 80px;
}
.link {
	background: none repeat scroll 0 0 rgba(255, 255, 255, 0.5);
	border-radius: 0.2em;
	color: inherit;
	padding: 0 0.1em;
	text-decoration: none;
	transition: all 0.5s ease 0s;
}
.link:hover, .link:focus {
	background: none repeat scroll 0 0 #FFFFFF;
}
.step {
	height: 700px;
	width: 900px;
	position: relative;
}

